mapper.select:
SELECT COUNT(*)
FROM menu
WHERE cafeId = #{cafeId} AND menuName = #{menuName}

SELECT menuOptionId,optionName,addPrice
FROM menu_option
WHERE menuId=#{menuId}

SELECT menuName,price,comment,menuPic
FROM menu
WHERE menuId=#{menuId}

mapper.update:
UPDATE menu


        categoryId = #{categoryId},


        menuName = #{menuName},


        price = #{price},


        comment = #{comment},


        menuPic = #{menuPic}


WHERE cafeId = #{cafeId}
AND menuId = #{menuId}

mapper.delete:
DELETE
FROM menu
WHERE menuId = #{menuId}
AND cafeId = #{cafeId}
