mapper.select:
SELECT T2.* FROM ( SELECT orderId FROM `order` WHERE userId = #{signedUserId}
and cafeId = #{cafeId}
ORDER BY orderId DESC LIMIT #{startIdx}, #{size} ) T1 INNER JOIN( SELECT a.orderId AS orderId, b.nickName AS nickName, c.cafeName AS cafeName, c.location AS location, a.pickUpTime AS pickUpTime, a.createdAt AS createdAt, a.memo AS memo, a.orderProgress AS orderProgress, d.orderMenuId AS orderMenuId, e.menuName AS menuName, e.price AS price, d.count AS count, g.menuOptionId AS menuOptionId, g.optionName AS optionName, g.addPrice AS addPrice FROM `order` a INNER JOIN `user` b ON a.userId = b.userId INNER JOIN `cafe` c ON c.cafeId = a.cafeId LEFT JOIN `order_menu` d ON a.orderId = d.orderId INNER JOIN `menu` e ON d.menuId = e.menuId LEFT JOIN `order_menu_option` f ON d.orderMenuId = f.orderMenuId INNER JOIN `menu_option` g ON f.menuOptionId = g.menuOptionId ) T2 ON T1.orderId = T2.orderId ORDER BY T2.orderId DESC

mapper.update:
update `order`
    set orderProgress = 5
    where orderId = #{orderId}
    and userId = #{signedUserId}
